import { Component, OnInit, Inject, Input } from '@angular/core';
import * as jsPDF from 'jspdf';

import { LocalizationService } from '../l10n/l10n.service';
import { FeatureToggleService } from '../feature-toggle/feature-toggle.service';
import { CvItem } from '../cv-item/cv-item';
import { CvItemService } from '../cv-item/cv-item.service';

@Component({
  selector: 'app-pdf',
  templateUrl: './pdf.component.html',
  styleUrls: ['./pdf.component.css'],
  providers: [
    { provide: 'Window', useValue: window, },
  ],
})
export class PdfComponent implements OnInit {
  public l10n;
  public featureToggles;
  @Input() public color = 'default';
  public verticalPosition = 50; // starting point
  public maximumHorizontalLength = 168;
  public generalData;
  public interestItems;
  public cvItems: CvItem[];
  public educationItems: CvItem[];
  public publicationItems: CvItem[];
  public talkItems: CvItem[];
  public certificationItems: CvItem[];
  public languageItems;
  public projectItems: CvItem[];
  public volunteerItems: CvItem[];
  public contactItems;

  constructor(
    @Inject('Window') private window: Window,
    private localizationService: LocalizationService,
    private featureToggleService: FeatureToggleService,
    private cvItemService: CvItemService,
  ) {
    this.localizationService.languageChanged.subscribe((data) => {
      this.l10n = data;
      this.getItems();
    });
  }

  private getLocalization(): void {
    this.l10n = this.localizationService.getDefault();
  }

  private getFeatureToggles(): void {
    this.featureToggles = this.featureToggleService.getFeatureToggles();
  }

  private getItems(): void {
    this.generalData = this.cvItemService.getGeneralData();
    this.cvItemService
      .getInterestItems()
      .then(items => this.interestItems = items);
    this.cvItemService
      .getCvItems()
      .then(items => this.cvItems = items);
    this.cvItemService
      .getEducationItems()
      .then(items => this.educationItems = items);
    this.cvItemService
      .getCertificationItems()
      .then(items => this.certificationItems = items);
    this.cvItemService
      .getLanguageItems()
      .then(items => this.languageItems = items);
    this.cvItemService
      .getPublicationItems()
      .then(items => this.publicationItems = items);
    this.cvItemService
      .getTalkItems()
      .then(items => this.talkItems = items);
    this.cvItemService
      .getProjectItems()
      .then(items => this.projectItems = items);
    this.cvItemService
      .getVolunteerItems()
      .then(items => this.volunteerItems = items);
    this.cvItemService
      .getContactItems()
      .then(items => this.contactItems = items);
  }

  ngOnInit() {
    this.getLocalization();
    this.getFeatureToggles();
    this.getItems();
  }

  public download() {
    if (this.featureToggles.downloadable_pdf === false) {
      return;
    }

    const pdf = this.generatePdfDocument(new jsPDF());

    // Save the PDF
    pdf.save('AngularCV_' + this.generalData.name.replace(' ', '_') + '.pdf');
  }

  generatePdfDocument(pdf) {
    pdf.page = 1;
    pdf.setCreationDate(new Date());
    pdf.setProperties({
      title: 'AngularCV - ' + this.generalData.name + ' - ' + this.generalData.position,
      subject: 'AngularCV',
      author: this.generalData.name,
      keywords: 'AngularCV, cv',
      creator: 'https://github.com/StegSchreck/AngularCV'
    });

    this.addPageHeader(pdf);

    this.addOverviewPageContent(pdf);
    if (this.cvItems !== undefined && this.cvItems !== [] && this.cvItems.length > 0) {
      this.switchPage(pdf);
      this.addExperiencePageContent(pdf);
    }
    if (this.educationItems !== undefined && this.educationItems !== [] && this.educationItems.length > 0) {
      this.switchPage(pdf);
      this.addEducationPageContent(pdf);
    }
    if (this.certificationItems !== undefined && this.certificationItems !== [] && this.certificationItems.length > 0) {
      this.switchPage(pdf);
      this.addCertificatesPageContent(pdf);
    }
    if (this.publicationItems !== undefined && this.publicationItems !== [] && this.publicationItems.length > 0) {
      this.switchPage(pdf);
      this.addPublicationsPageContent(pdf);
    }
    if (this.talkItems !== undefined && this.talkItems !== [] && this.talkItems.length > 0) {
      this.switchPage(pdf);
      this.addTalksPageContent(pdf);
    }
    if (this.projectItems !== undefined && this.projectItems !== [] && this.projectItems.length > 0) {
      this.switchPage(pdf);
      this.addProjectsPageContent(pdf);
    }
    if (this.volunteerItems !== undefined && this.volunteerItems !== [] && this.volunteerItems.length > 0) {
      this.switchPage(pdf);
      this.addVolunteeringPageContent(pdf);
    }

    this.addPageFooter(pdf);

    return pdf;
  }

  private switchPage(pdf) {
    this.addPageFooter(pdf);
    pdf.addPage();
    pdf.page++;
    this.addPageHeader(pdf);
  }

  private addOverviewPageContent(pdf) {
    this.addGeneralDescription(pdf);
    this.verticalPosition += 20;
    this.addContactData(pdf);
    this.addLanguages(pdf);
    this.verticalPosition += 10;
    this.addInterests(pdf);
  }

  private addGeneralDescription(pdf) {
    pdf.setFontSize(14);
    pdf.setFontType('bold');
    pdf.text(this.l10n.overview.subtitle_aboutme.toUpperCase(), 20, this.verticalPosition);
    this.verticalPosition += 10;
    pdf.setFontSize(12);
    pdf.setFontType('normal');
    const splittedDescription = pdf.splitTextToSize(this.generalData.description, this.maximumHorizontalLength);
    pdf.text(splittedDescription, 20, this.verticalPosition);
  }

  private addContactData(pdf) {
    if (this.contactItems !== undefined && this.contactItems !== [] && this.contactItems.length > 0) {
      this.addLine(pdf, this.l10n.pdf.label_city.toUpperCase(), this.contactItems.city);
      this.addLine(pdf, this.l10n.pdf.label_email.toUpperCase(), this.contactItems.mail);
      this.addLine(pdf, this.l10n.contact.phone.toUpperCase(), this.contactItems.phone);
      this.addLine(pdf, 'SKYPE', this.contactItems.skype);
      this.addLine(pdf, 'LINKEDIN', this.contactItems.linkedin);
      this.addLine(pdf, 'XING', this.contactItems.xing);
      this.addLine(pdf, 'GITHUB', this.contactItems.github);
      this.addLine(pdf, 'STACKOVERFLOW', this.contactItems.stackoverflow);
      this.addLine(pdf, 'TWITTER', this.contactItems.twitter);
    }
  }

  private addLanguages(pdf) {
    if (this.languageItems !== undefined && this.languageItems !== [] && this.languageItems.length > 0) {
      this.verticalPosition += 10;
      pdf.setFontSize(14);
      pdf.setFontType('bold');
      pdf.text(this.l10n.education.title_languages.toUpperCase(), 20, this.verticalPosition);
      this.verticalPosition += 10;
      pdf.setFontSize(12);
      pdf.setFontType('normal');
      for (const item of this.languageItems) {
        const bar_length: number = 168 * (item.level / 100);

        pdf.setDrawColor(0);
        pdf.setFillColor(68, 68, 68);
        pdf.rect(20, this.verticalPosition - 4, bar_length, 5.5, 'F');
        pdf.setDrawColor(0);
        pdf.setFillColor(222, 222, 222);
        pdf.rect(bar_length + 20, this.verticalPosition - 4, 168 - bar_length, 5.5, 'F');

        pdf.setTextColor(222);
        pdf.text(item.title, 25, this.verticalPosition);
        pdf.setTextColor(0);

        this.verticalPosition += 7;
      }
    }
  }

  private addInterests(pdf) {
    if (this.interestItems !== undefined && this.interestItems !== [] && this.interestItems.length > 0) {
      pdf.setFontSize(14);
      pdf.setFontType('bold');
      pdf.text(this.l10n.overview.subtitle_interests.toUpperCase(), 20, this.verticalPosition);
      this.verticalPosition += 10;
      pdf.setFontSize(12);
      pdf.setFontType('normal');
      let interests = '';
      for (const item of this.interestItems) {
        if (interests !== '') { interests = interests + ', '; }
        interests = interests + item.title;
      }
      const splittedInterests = pdf.splitTextToSize(interests, this.maximumHorizontalLength);
      pdf.text(splittedInterests, 20, this.verticalPosition);
    }
  }

  private addLine(pdf, title: string, content: string) {
    if (content !== undefined && content !== '') {
      pdf.setFontSize(12);
      pdf.setFontType('bold');
      pdf.text(title, 20, this.verticalPosition);
      pdf.setFontSize(11);
      pdf.setFontType('normal');
      pdf.text(content, 80, this.verticalPosition);
      this.verticalPosition += 7;
    }
  }

  private addExperiencePageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.experience.title);
    this.addCvItems(pdf, this.cvItems, true);
  }

  private addProjectsPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.projects.title);
    this.addCvItems(pdf, this.projectItems);
  }

  private addVolunteeringPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.volunteering.title);
    this.addCvItems(pdf, this.volunteerItems);
  }

  private addEducationPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.education.title_education);
    this.addCvItems(pdf, this.educationItems);
  }

  private addCertificatesPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.education.title_certificates);
    this.addCvItems(pdf, this.certificationItems);
  }

  private addPublicationsPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.publications.title_publications);
    this.addCvItems(pdf, this.publicationItems);
  }

  private addTalksPageContent(pdf) {
    this.addPageTitle(pdf, this.l10n.publications.title_talks);
    this.addCvItems(pdf, this.talkItems);
  }

  private addPageTitle(pdf, title: string) {
    pdf.setFontSize(14);
    pdf.setFontType('bold');
    pdf.text(title.toUpperCase(), 20, this.verticalPosition);
    this.verticalPosition += 15;
  }

  private addCvItems(pdf, items: CvItem[], addVerticalLine?: Boolean) {
    for (const item of items) {
      this.splitPageIfNecessary(pdf);
      this.addCvItem(pdf, item, addVerticalLine);
    }
  }

  private splitPageIfNecessary(pdf) {
    if (this.verticalPosition >= 235) {
      pdf.setTextColor(68, 68, 68);
      pdf.setFontType('bolditalic');
      pdf.setFontSize(10);
      this.verticalPosition += 5;
      pdf.text(this.l10n.pdf.continuing_next_page, 20, this.verticalPosition);
      this.switchPage(pdf);
      pdf.setTextColor(68, 68, 68);
      pdf.setFontType('bolditalic');
      pdf.setFontSize(10);
      pdf.text(this.l10n.pdf.continued_from_last_page, 20, this.verticalPosition);
      pdf.setTextColor(0, 0, 0);
      pdf.setFontType('normal');
      this.verticalPosition = 65;
    }
  }

  private addCvItem(pdf, item: CvItem, addVerticalLine?: Boolean) {
    const startPosition = this.verticalPosition;
    pdf.setDrawColor(0);
    pdf.setFillColor(0, 0, 0);
    pdf.circle(22, this.verticalPosition - 1, 2, 'F');

    this.addCvItemTitle(pdf, item);
    this.addCvItemTimespan(pdf, item);
    this.addCvItemDescription(pdf, item);
    this.addCvItemTags(pdf, item);

    this.verticalPosition += 10;
    if (addVerticalLine) {
      pdf.line(22, startPosition, 22, this.verticalPosition); // vertical line
    }
  }

  private addCvItemTitle(pdf, item: CvItem) {
    pdf.setFontSize(12);
    pdf.setFontType('bold');
    pdf.text(item.title, 30, this.verticalPosition);
    this.verticalPosition += 5;

    pdf.setFontSize(12);
    pdf.setFontType('normal');
    pdf.setTextColor(68, 68, 68);
    pdf.text(item.subtitle, 30, this.verticalPosition);
    this.verticalPosition += 5;
  }

  private addCvItemTimespan(pdf, item: CvItem) {
    pdf.setFontSize(10);
    pdf.setFontType('normal');
    let end = item.end;
    if (item.begin !== undefined && item.begin !== '' && (item.end === undefined || item.end === '')) {
      end = 'now';
    }
    let time = '';
    if (item.begin !== undefined && item.begin !== '') {
      time = item.begin + ' - ' + end;
    } else {
      time = end;
    }
    pdf.text(time, 30, this.verticalPosition);
  }

  private addCvItemTags(pdf, item: CvItem) {
    pdf.setFontSize(10);
    pdf.setFontType('normal');
    pdf.setFont('courier');
    pdf.setTextColor(68, 68, 68);
    if (item.tags !== undefined && item.tags.length > 0) {
      this.verticalPosition += 7;
      let tags = '';
      for (const tag of item.tags) {
        if (tags !== '') {
          tags = tags + ', ';
        }
        tags = tags + tag;
      }
      const splittedTags = pdf.splitTextToSize(tags, this.maximumHorizontalLength - 10);
      pdf.text(splittedTags, 30, this.verticalPosition);
      this.verticalPosition += 3.5 * (splittedTags.length - 1);
    }
    pdf.setFont('helvetica');
    pdf.setTextColor(0, 0, 0);
  }

  private addCvItemDescription(pdf, item: CvItem) {
    if (item.description !== undefined && item.description !== '') {
      this.verticalPosition += 5;
      pdf.setFontSize(10);
      pdf.setFontType('italic');
      pdf.setTextColor(0, 0, 0);
      const splittedDescription = pdf.splitTextToSize(item.description, this.maximumHorizontalLength - 10);
      pdf.text(splittedDescription, 30, this.verticalPosition);
      this.verticalPosition += 4 * (splittedDescription.length - 1);
    }
  }

  private addPageHeader(pdf) {
    pdf.setFont('courier');
    pdf.setFontType('bold');
    pdf.setFontSize(24);
    pdf.setTextColor(48, 48, 48);
    pdf.text(this.generalData.name, 20, 20);
    pdf.setFont('helvetica');
    pdf.setFontType('normal');
    pdf.setFontSize(18);
    pdf.setTextColor(68, 68, 68);
    pdf.text(this.generalData.position, 20, 30);
    pdf.setDrawColor(68, 68, 68);
    pdf.line(20, 35, 188, 35); // horizontal line
    this.verticalPosition = 50;
    pdf.setTextColor(0, 0, 0);
  }

  private addPageFooter(pdf) {
    pdf.line(20, 280, 188, 280); // horizontal line
    pdf.setFont('helvetica');
    pdf.setFontType('normal');
    pdf.setFontSize(7);
    pdf.text(this.l10n.pdf.info_generation, 20, 284);
    pdf.setFontSize(7);
    pdf.text(this.l10n.pdf.info_headhunter, 20, 287);
    // tslint:disable-next-line:max-line-length
    const imgData = 'data:image/png;base64,';
    pdf.addImage(imgData, 'JPEG', 168, 281, 19, 6);
  }
}
